mapper.select:
SELECT odr.rider_id riderId, odr.arrival_time arrivalTime, addr.building_management_number buildingManagementNumber, addr.city_name cityName, addr.city_country_name cityCountryName, addr.town_name townName, addr.road_name roadName, addr.building_number buildingNumber, addr.building_side_number buildingSideNumber, odr.address_detail addressDetail, shop.id shopId, shop.name shopName, odr.id order_id, odr.delivery_cost deliveryCost, odr.order_status orderStatus FROM ORDERS odr INNER JOIN SHOP shop ON (odr.shop_id = shop.id) INNER JOIN ADDRESS addr ON (odr.address_code = ADDRESS.building_management_number) WHERE odr.rider_id = #{riderId}
AND odr.id > #{lastViewedOrderId}
ORDER BY odr.id DESC
