mapper.select:
SELECT id, password, name, tel, mail, status, created_at createdAt, updated_at updatedAt, address, address_detail addressDetail
FROM   MEMBER
WHERE  id = #{id}
AND	   password = #{password}
AND	   status != 'DELETE'

SELECT COUNT(id)
  FROM MEMBER
 WHERE id = #{id}

SELECT  SUBSTRING(address_code, 1, 10)
FROM    MEMBER
WHERE   id = #{id}
