mapper.update:
UPDATE MENU
SET status = "DELETED"
WHERE id = #{id}

mapper.select:
SELECT COUNT(id)
FROM MENU
WHERE menu_Group_id = #{menuGroupId}
AND id = #{menuId}

mapper.update:
