mapper.select:
SELECT odr.id orderId, odr.start_time orderStartTime, odr.order_status orderStatus, odr.ex_arrival_time orderExArrivalTime, odr.arrival_time orderArrivalTime, odr.rider_id orderRiderId, odr.address_code orderAddressCode, odr.address_detail orderAddressDetail, odr.member_id memberId, odr.delivery_cost deliveryCost, shop.id shopId, shop.name shopName, item.id itemId, menu.id menuId, menu.name menuName, menu.price menuPrice, item.count itemCount, item_opt.id itemOptionId, opt.id optionId, opt.name optionName, opt.price optionPrice, cpn_isu.id couponIssueId, cpn.id couponId, cpn.name couponName, cpn.discount_type discountType, cpn.discount_value discountValue, pay.amount_discount discountPrice FROM ORDERS odr LEFT OUTER JOIN ORDERS_ITEM item ON (odr.id = item.order_id) LEFT OUTER JOIN ORDERS_ITEM_OPTION item_opt ON (item.id = item_opt.order_item_id) INNER JOIN MENU menu ON (menu.id = item.menu_id) LEFT OUTER JOIN OPTION opt ON (opt.menu_id = menu.id) INNER JOIN SHOP shop ON (odr.shop_id = shop.id) INNER JOIN MEMBER member ON (odr.member_id = member.id) INNER JOIN PAYMENT pay ON (pay.order_id = odr.id) LEFT OUTER JOIN COUPON_ISSUE cpn_isu ON (pay.id = cpn_isu.payment_id) LEFT OUTER JOIN COUPON cpn ON (cpn_isu.coupon_id = cpn.id) WHERE member.id = #{memberId}
AND odr.id > #{lastViewedOrderId}
ORDER BY odr.id DESC

SELECT 
        (SELECT m.price 
                          +
                          (SELECT SUM(opt.price)
                           FROM OPTION opt
                           WHERE  opt.id IN (#{option.optionId}))
                           
         FROM MENU m
         WHERE  m.id = #{item.menuId}
        ) * #{item.count}

SELECT m.id menuId, m.name menuName, m.price menuPrice, opt.id optionId, opt.name optionName, opt.price optionPrice FROM MENU m LEFT OUTER JOIN OPTION opt ON (m.id = opt.menu_id) WHERE m.id IN (#{menu.menuId}) AND opt.id IN (
#{option.optionId}
)

mapper.update:
UPDATE ORDERS
SET order_status =  #{status}
WHERE id = #{orderId}

UPDATE  ORDERS
SET     order_status = 'ORDER_APPROVAL',
        ex_arrival_time = #{exArrivalTime}
WHERE   id = #{orderId}

mapper.select:
SELECT  odr.memberId memberId
FROM    ORDERS odr
WHERE   odr.id = #{orderId}

SELECT  odr.order_status orderStatus
FROM    ORDERS odr
WHERE   odr.id = #{orderId}

mapper.update:
UPDATE ORDERS
SET    order_status = 'DELIVERY_COMPLETE',
       arrival_time = #{completeTime}
WHERE  id = #{orderId}
