mapper.select:
SELECT COUNT(*)
FROM SHOP
WHERE owner_id = #{ownerId}

SELECT COUNT(*)
FROM SHOP
WHERE id = #{shopId}
AND   owner_id = #{ownerId}

mapper.update:
UPDATE SHOP
SET work_condition = 'OPEN'
WHERE id = #{shopId}

UPDATE SHOP
SET work_condition = 'CLOSE'
WHERE id=#{shopId}

mapper.select:
SELECT  COUNT(*)
FROM    SHOP A JOIN DELIVERY_LOCATION B
ON      A.id = B.shop_id
WHERE   A.owner_id = #{ownerId}
AND     B.id = #{deliveryLocationId}
